sys_script_include.script:
var ProfileManagementAJAX = Class.create();
ProfileManagementAJAX.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	//Function for returning an
	getComparisonData: function(){
		try {
			var json = new global.JSON();
			var profileId = this.getParameter('sysparm_profile_id');
			var userId = this.getParameter('sysparm_user_id');
			var profileSkill = {};
			var userProfileSkills = [];
			var matched = false;
			if (!userId || !profileId){
				return json.encode(userProfileSkills);
			}
			var skillId = null;
			var grProfileSkill = new GlideRecord('x_78492_profiling_role_profile_skill');
			grProfileSkill.addQuery('role_profile', profileId);
			grProfileSkill.orderBy('skill.u_category');
			grProfileSkill.orderBy('skill.name');
			//profileSkill.groupBy('category');
			grProfileSkill.query();
			while (grProfileSkill.next()){
				skillId = grProfileSkill.getValue('skill');
				profileSkill = {
					skill_id : skillId, 
					category : grProfileSkill.skill.u_category.getDisplayValue(), 
					skill: grProfileSkill.getDisplayValue('skill'), 
					level : grProfileSkill.getDisplayValue('level'), 
					level_value : grProfileSkill.getValue('level'), 
					matched : 'no'
				};
				userProfileSkills.push(profileSkill);

			}
			var grUserSkill = new GlideRecord('sys_user_has_skill');
			grUserSkill.addQuery('user', userId);
			grUserSkill.query();
			while (grUserSkill.next()){
				matched = false;
				skillId = grUserSkill.getValue('skill');
				for (var i = 0; i < userProfileSkills.length; i++){
					if (userProfileSkills[i].skill_id == skillId){
						matched = true;
						var upf = userProfileSkills[i];
						var skillLevel = grUserSkill.getValue('u_skill_level');
						if (upf.level_value == skillLevel){
							upf.matched = 'equal';
						}
						else if (upf.level_value < skillLevel){
							upf.matched = 'higher';
						}
						else {
							upf.matched = 'lower';
						}
					}
				}
				if (!matched){
		          profileSkill = {
					skill_id : skillId, 
					category : grUserSkill.skill.u_category.getDisplayValue(), 
					skill: grUserSkill.getDisplayValue('skill'), 
					level : grUserSkill.getDisplayValue('u_skill_level'), 
					level_value : grProfileSkill.getValue('u_skill_level'), 
					matched : 'user'
				  };
				userProfileSkills.push(profileSkill);
				}
			}

			return json.encode(userProfileSkills);
		}

		catch(err){
			gs.error('Error detected!! Error is: ' + err.message);
			return null;
		}

	},

	type: 'ProfileManagementAJAX'
});
